    });
  }

  getRepositoryTypeLabel(type: string): string {
    return type === 'github' ? 'Git Repository' : 'Local directory';
  }
}

import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AppHeader, Button, Card, FormInput, Badge } from 'endpoint-engineering-components';
import { ALaCarteRequestService } from '../../services/alacarte-request.service';
import { ALaCarteRequest, OutputType, RepositoryConfig } from '../../models/alacarte-request.model';

@Component({
  selector: 'app-request-edit',
  imports: [CommonModule, FormsModule, AppHeader, Button, Card, FormInput, Badge],
  templateUrl: './request-edit.html',
  styleUrl: './request-edit.scss'
})
export class RequestEdit implements OnInit {
  request?: ALaCarteRequest;
  requestName = '';
  solutionName = '';
  outputDirectory = '';
  outputType: OutputType = 'dotnet';
  repositories: RepositoryConfig[] = [];
  loading = true;

  outputTypes: { value: OutputType; label: string }[] = [
    { value: 'dotnet', label: 'DotNet Solution (.sln)' },
    { value: 'angular', label: 'Angular Workspace' },
    { value: 'node', label: 'Node.js Package' },
    { value: 'python', label: 'Python Project' },
    { value: 'custom', label: 'Custom' }
  ];

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private requestService: ALaCarteRequestService
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.requestService.getRequest(id).subscribe(request => {
        if (request) {
          this.request = request;
          this.requestName = request.name;
          this.solutionName = request.solutionName;
          this.outputDirectory = request.outputDirectory;
          this.outputType = request.outputType;
          this.repositories = [...request.repositories];
        }
        this.loading = false;
      });
    }
  }

  onBrowseDirectory(): void {
    console.log('Browse directory');
  }

  onAddRepository(): void {
    console.log('Add repository');
  }

  onEditRepository(repo: RepositoryConfig): void {
    console.log('Edit repository', repo);
  }

  onDeleteRepository(repo: RepositoryConfig): void {
    this.repositories = this.repositories.filter(r => r.id !== repo.id);
  }

  onCancel(): void {
    if (this.request) {
      this.router.navigate(['/requests', this.request.id]);
    } else {
      this.router.navigate(['/requests']);
    }
  }

  onSave(): void {
    if (!this.request || !this.requestName || !this.solutionName || !this.outputDirectory) {
      alert('Please fill in all required fields');
      return;
    }

    this.requestService.updateRequest(this.request.id, {
      name: this.requestName,
      solutionName: this.solutionName,
      outputDirectory: this.outputDirectory,
      outputType: this.outputType,
      repositories: this.repositories
    }).subscribe(updated => {
      if (updated) {
        this.router.navigate(['/requests', updated.id]);
      }
    });
  }

  getRepositoryTypeLabel(type: string): string {
    return type === 'github' ? 'Git Repository' : 'Local directory';
  }
}

import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { AppHeader, Button, Card, StatusIndicator } from 'endpoint-engineering-components';
import { ALaCarteRequestService } from '../../services/alacarte-request.service';
import { ALaCarteRequest, ExecutionResult } from '../../models/alacarte-request.model';

@Component({
  selector: 'app-request-execute',
  imports: [CommonModule, AppHeader, Button, Card, StatusIndicator],
  templateUrl: './request-execute.html',
  styleUrl: './request-execute.scss'
})
export class RequestExecute implements OnInit {
  request?: ALaCarteRequest;
  executionResult?: ExecutionResult;
  executing = false;
  executionProgress = 0;
  currentStep = '';
  logs: string[] = [];

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private requestService: ALaCarteRequestService
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.requestService.getRequest(id).subscribe(request => {
        this.request = request;
        this.startExecution();
      });
    }
  }

  startExecution(): void {
    if (!this.request) return;

    this.executing = true;
    this.logs = [];
    this.simulateProgress();

    this.requestService.executeRequest(this.request.id).subscribe({
      next: (result) => {
        this.executionResult = result;
        this.executing = false;
        this.executionProgress = 100;
        this.currentStep = 'Completed';
        this.logs.push(' Execution completed successfully!');
        
        if (result.outputPath) {
          this.logs.push(` Output saved to: ${result.outputPath}`);
        }
        
        if (result.generatedFiles) {
          this.logs.push(` Generated ${result.generatedFiles.length} files`);
        }
      },
      error: (err) => {
        this.executing = false;
        this.executionResult = {
          requestId: this.request!.id,
          status: 'error',
          errors: [err.message || 'Execution failed']
        };
        this.logs.push(' Execution failed');
      }
    });
  }

  simulateProgress(): void {
    const steps = [
      { progress: 10, step: 'Validating configuration', delay: 200 },
      { progress: 20, step: 'Cloning repositories', delay: 500 },
      { progress: 40, step: 'Processing templates', delay: 800 },
      { progress: 60, step: 'Generating solution structure', delay: 600 },
      { progress: 80, step: 'Writing files', delay: 400 },
      { progress: 90, step: 'Finalizing output', delay: 300 }
    ];

    let currentIndex = 0;
    const updateProgress = () => {
      if (currentIndex < steps.length && this.executing) {
        const { progress, step, delay } = steps[currentIndex];
        this.executionProgress = progress;
        this.currentStep = step;
        this.logs.push(` ${step}...`);
        currentIndex++;
        setTimeout(updateProgress, delay);
      }
    };

    updateProgress();
  }

  onViewOutput(): void {
    if (this.request) {
      this.router.navigate(['/output', this.request.id]);
    }
  }

  onBackToRequest(): void {
    if (this.request) {
      this.router.navigate(['/requests', this.request.id]);
    }
  }

  onBackToList(): void {
    this.router.navigate(['/requests']);
  }
}

import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';
import { AppHeader, Button, Card, SearchBox, EmptyState, StatusIndicator } from 'endpoint-engineering-components';
import { ALaCarteRequestService } from '../../services/alacarte-request.service';
import { ALaCarteRequest } from '../../models/alacarte-request.model';

@Component({
  selector: 'app-requests-list',
  imports: [CommonModule, AppHeader, Button, Card, SearchBox, EmptyState, StatusIndicator],
  templateUrl: './requests-list.html',
  styleUrl: './requests-list.scss'
})
export class RequestsList implements OnInit {
  requests: ALaCarteRequest[] = [];
  filteredRequests: ALaCarteRequest[] = [];
  searchQuery = '';

  constructor(
    private requestService: ALaCarteRequestService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.requestService.getRequests().subscribe(requests => {
      this.requests = requests;
      this.filteredRequests = requests;
    });
  }

  onSearch(query: string): void {
    this.searchQuery = query;
    if (!query.trim()) {
      this.filteredRequests = this.requests;
      return;
    }
    
    const lowerQuery = query.toLowerCase();
    this.filteredRequests = this.requests.filter(req =>
      req.name.toLowerCase().includes(lowerQuery) ||
      req.solutionName.toLowerCase().includes(lowerQuery) ||
      req.description?.toLowerCase().includes(lowerQuery)
    );
  }

  onCreateNew(): void {
    this.router.navigate(['/requests/create']);
  }

  onViewRequest(request: ALaCarteRequest): void {
    this.router.navigate(['/requests', request.id]);
  }

  onEditRequest(request: ALaCarteRequest, event: Event): void {
    event.stopPropagation();
    this.router.navigate(['/requests', request.id, 'edit']);
  }

  onExecuteRequest(request: ALaCarteRequest, event: Event): void {
    event.stopPropagation();
    this.router.navigate(['/requests', request.id, 'execute']);
  }

  onDeleteRequest(request: ALaCarteRequest, event: Event): void {
    event.stopPropagation();
    // Delete dialog will be shown
    if (confirm(`Delete "${request.name}"?`)) {
      this.requestService.deleteRequest(request.id).subscribe();
    }
  }

  getStatusLabel(status: string): string {
    const labels: Record<string, string> = {
      draft: 'Draft',
      ready: 'Ready',
      executing: 'Executing',
      completed: 'Completed',
      failed: 'Failed'
    };
    return labels[status] || status;
  }

  getStatusType(status: string): 'success' | 'warning' | 'error' | 'info' {
    const types: Record<string, 'success' | 'warning' | 'error' | 'info'> = {
      draft: 'info',
      ready: 'success',
      executing: 'warning',
      completed: 'success',
      failed: 'error'
    };
    return types[status] || 'info';
  }

  formatDate(date: Date): string {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  }
}

